mapper.select:
SELECT a.id AS id, a.email AS email, a.sign_in_name as signInName, a.phone as phone, a.wx_open_id as wxOpenId, a.create_time AS createTime, a.update_time AS updateTime, a.enable_flag AS enableFlag, IF ( a.`password` = '', 0, 1 ) AS passwordFlag, IFNULL( MAX( c.create_time ), a.create_time) AS lastActiveTime, b.nickname AS nickname, b.avatar_file_id AS avatarFileId FROM sys_user a LEFT JOIN sys_user_info b ON b.id = a.id LEFT JOIN sys_request c ON c.create_id = a.id WHERE a.del_flag = FALSE
AND a.id = #{dto.id}
AND b.nickname LIKE CONCAT('%', #{dto.nickname}, '%')
AND a.sign_in_name LIKE concat('%', #{dto.signInName}, '%')
AND a.email LIKE concat('%', #{dto.email}, '%')
AND a.phone LIKE concat('%', #{dto.phone}, '%')
AND a.wx_open_id LIKE concat('%', #{dto.wxOpenId}, '%')
AND a.enable_flag = #{dto.enableFlag}
AND IF ( a.`password` = '', 0, 1 ) = #{dto.passwordFlag}
AND a.create_time >= #{dto.beginCreateTime}
AND a.create_time <= #{dto.endCreateTime}
AND IFNULL( (SELECT MAX(create_time) FROM sys_request WHERE create_id = a.id ), a.create_time) >= #{dto.beginLastActiveTime}
AND IFNULL( (SELECT MAX(create_time) FROM sys_request WHERE create_id = a.id ), a.create_time) <= #{dto.endLastActiveTime}
GROUP BY a.id
