mapper.select:
SELECT
a.tenant_id AS tenantId, a.id AS id, a.email AS email, a.sign_in_name as signInName, a.phone as phone, a.wx_open_id as wxOpenId, a.wx_app_id as wxAppId, a.create_time AS createTime, a.enable_flag AS enableFlag, IF ( a.`password` = '', 0, 1 ) AS passwordFlag, b.nickname AS nickname, b.avatar_file_id AS avatarFileId, b.last_active_time AS lastActiveTime, b.last_ip AS ip, b.last_region AS region, b.sign_up_type AS signUpType
FROM
sys_user a
LEFT JOIN sys_user_info b ON b.id = a.id
WHERE 1 = 1
AND a.id = #{dto.id}
AND b.nickname LIKE CONCAT('%', #{dto.nickname}, '%')
AND a.sign_in_name LIKE concat('%', #{dto.signInName}, '%')
AND a.email LIKE concat('%', #{dto.email}, '%')
AND a.phone LIKE concat('%', #{dto.phone}, '%')
AND a.wx_app_id LIKE concat('%', #{dto.wxAppId}, '%')
AND a.wx_open_id LIKE concat('%', #{dto.wxOpenId}, '%')
AND a.enable_flag = #{dto.enableFlag}
AND IF ( a.`password` = '', 0, 1 ) = #{dto.passwordFlag}
AND a.create_time >= #{dto.beginCreateTime}
AND a.create_time <= #{dto.endCreateTime}
AND b.last_active_time >= #{dto.beginLastActiveTime}
AND b.last_active_time <= #{dto.endLastActiveTime}
AND b.last_ip LIKE CONCAT('%', #{dto.ip}, '%')
AND b.last_region LIKE CONCAT('%', #{dto.region}, '%')
AND b.sign_up_type = #{dto.signUpType}
AND a.tenant_id IN
#{item}
